import sysimport jsonimport tangeloimport osimport time@tangelo.restfuldef get(filePath='./',filePolygon='polyFile.txt', fileAppOut='appliedScores.csv', fScoreThresh='0.005', dataSet="Cleveland", aggScores="false", useML="false", useBayes="false"):    timeStamp = str(time.time())    ssName = filePath + "applyScores_" + timeStamp + ".sh"    dataTypeString = "2"    dataSetString = "cleanClevelandTwitterData/allClevelandData.csv"    if dataSet=="Texas":        dataTypeString = "3"        dataSetString = "cleanTexasData3/"    elif dataSet=="dcArea":        dataTypeString = "3"        dataSetString = "cleanDcData/"        launchString = "spark-submit --py-files lib/shapeReader.py,lib/pointClass.py,lib/fspLib.py --executor-memory 45g --driver-memory 10g --total-executor-cores 500 findSimilarPlaces.py spark://sc04.silverdale.dev:7077 hdfs://scc.silverdale.dev:/user/jgartner/" + dataSetString +" inputFiles/" + filePolygon + " dict_"+ fileAppOut + " scoreFiles/" + fileAppOut + " -jobNm onlineApplication -datTyp " + dataTypeString + " -sThresh " + fScoreThresh    if aggScores=="false":        launchString = launchString + " --sit"    if useML != "false":        launchString = "spark-submit --py-files lib/shapeReader.py,lib/pointClass.py,lib/fspLib.py --executor-memory 45g --driver-memory 10g --total-executor-cores 500 aynWald_fsp.py spark://sc04.silverdale.dev:7077 hdfs://scc.silverdale.dev:/user/jgartner/" + dataSetString + " inputFiles/" + filePolygon + " scoreFiles/" + fileAppOut + " -jobNm onlineApplication -datTyp " + dataTypeString + " -sThresh " + fScoreThresh        if aggScores=="false":            launchString = launchString + " --sit"        if useBayes != "false":            launchString = launchString + " --useBayes"    f = open(ssName, 'w')    f.write(launchString)    f.close()    os.system("chmod u+x " + ssName)    os.chdir(filePath)    os.system("nohup " + ssName + " >& consoleApply_" + timeStamp + ".txt &")    return "0"