import sysimport jsonimport tangeloimport osimport timesys.path.append(".")from decorators import allow_all_origins@tangelo.restful@allow_all_originsdef get(filePath='./', fileTestOut='testSummary.csv'):    ssName = filePath + "testFiles/" + fileTestOut    retDict = {}    lPasPer = []    lNonZero= []    f = open(ssName, 'r')    for line in f:        nums = line.split(', ')        if len(nums)==3:            retDict["m"] = nums[0]            retDict["v"] = nums[1]        else:            lNonZero.append(int(nums[2]))            try:                pasPer = (1.*int(float(nums[3])*1000000./float(nums[2])))/10000.                lPasPer.append(pasPer)            except ZeroDivisionError:                lPasPer.append(-1)    f.close()    retDict["lPas"] = lPasPer    retDict["lNZ"] = lNonZero    return json.dumps(retDict)