import sysimport jsonimport tangeloimport osimport time@tangelo.restfuldef get(filePath="./",filePolygon="polyFile.txt", fileTestOut="testSummary.csv", dataSet="Clevland"):    timeStamp = str(time.time())    ssName = filePath + "testPolygons_" + timeStamp + ".sh"    dataTypeString = "2"    dataSetString = "cleanClevelandTwitterData/allClevelandData.csv"    if dataSet=="Texas":        dataTypeString = "3"        dataSetString = "cleanTexasData3/"    if dataSet=="dcArea":        dataTypeString = "3"        dataSetString = "cleanDcData/"    launchString = "spark-submit --py-files lib/shapeReader.py,lib/pointClass.py,lib/fspLib.py --executor-memory 60g --driver-memory 10g --total-executor-cores 400  testModel.py spark://sc04.silverdale.dev:7077 hdfs://scc.silverdale.dev:/user/jgartner/" + dataSetString + " inputFiles/" + filePolygon + " " + fileTestOut + " -jobNm onlineTest -datTyp " + dataTypeString    f = open(ssName, 'w')    f.write(launchString)    f.close()    os.system("chmod u+x " + ssName)    os.chdir(filePath)    os.system("nohup " + ssName + " >& consoleTest_" + timeStamp + ".txt &")    return "0"