import sysimport tangelosys.path.append(".")import conffrom decorators import allow_all_originsfrom decorators import validate_user@tangelo.restful@allow_all_origins@validate_userdef get(user='demo',filePolygon='polyFile',fileString=''):    confObj = conf.get()    filePath = confObj['root_data_path'] +'/' +user    fileName = filePath + "/inputFiles/" + filePolygon    f = open(fileName, 'w')    f.write(fileString)    f.close()    return "Successfull wrote " + fileName    