import sysimport tangelosys.path.append(".")import conffrom decorators import allow_all_originsfrom decorators import validate_user#TODO, this should change to a POST@tangelo.restful@allow_all_origins@validate_userdef get(user='demo',filePolygon='polyFile',fileString=''):    """    Save a polygon set    :param user: username    :param filePolygon: name of polygon set    :param fileString: polygon set data.    :return:  "wrote <filePolygon>" returned if successful.    """    confObj = conf.get()    filePath = confObj['root_data_path'] +'/' +user    fileName = filePath + "/inputFiles/" + filePolygon    f = open(fileName, 'w')    f.write(fileString)    f.close()    return "wrote " + fileName    