import sysimport jsonimport tangeloimport osimport time@tangelo.restfuldef get(filePath='./', fileAppOut='appliedScores.csv', maxOut = "-1", bIgnorDict="false"):    ssName = filePath + "scoreFiles/" + fileAppOut    retDict = {}    lLats    = []    lLons    = []    lCaption = []    lScore   = []    f = open(ssName, 'r')    nRet = 0    for line in f:        nums = line.split('\t')        try:            lLats.append(nums[0])            lLons.append(nums[1])            lCaption.append(nums[2])            lScore.append(str((int(float(nums[3])*10000.)*1.)/10000))        except:            continue        nRet = nRet + 1        if nRet == int(maxOut):            break    f.close()    if bIgnorDict=="false":        dictName = filePath + "dictFiles/dict_" + fileAppOut        lWordScores = []        f2 = open(dictName,'r')        for line in f2:            lWordScores.append(line.split('\t'))        lWordClean = map(lambda x: [x[0], x[2], x[4], str((1.*int(float(x[5])*10000.))/10000.)], lWordScores)        lWordSorted = sorted(lWordClean,key=lambda x: float(x[3]),reverse=True)        retDict["dic"] = lWordSorted        retDict["total"] = nRet    retDict["lat"] = lLats    retDict["lon"] = lLons    retDict["cap"] = lCaption    retDict["sco"] = lScore    return json.dumps(retDict)